using System;
using System.Collections.Generic;
using System.Linq;
using System.Reflection;
using HtmlAgilityPack;
using Warface.Entities.Shop;
using Xunit;

namespace Warface.Entities.Tests
{
    public class ShopOffer_Tests
    {
        [Theory]
        [MemberData(nameof(ShopOffer_Tests_DataGenerator.GetALotOfOfferNodes), MemberType = typeof(ShopOffer_Tests_DataGenerator))]
        public void ParseNode(HtmlNode offerNode)
        {
            ShopOffer.ParseNode(offerNode);
        }

        [Theory]
        [MemberData(nameof(ShopOffer_Tests_DataGenerator.GetOfferNodesAndExpectedPropertyValues), MemberType = typeof(ShopOffer_Tests_DataGenerator))]
        public void ParseNode_ManualCheck(HtmlNode offerNode, object expectedValues)
        {
            var offer = ShopOffer.ParseNode(offerNode);

            foreach (var offerPropertyInfo in typeof(ShopOffer).GetProperties(BindingFlags.Instance | BindingFlags.Public))
            {
                var dynamicsPropertyInfo = expectedValues.GetType().GetProperty(offerPropertyInfo.Name);
                if (dynamicsPropertyInfo == null)
                {
                    Assert.ThrowsAny<TargetInvocationException>(GetOfferPropertyValue);
                    continue;
                }

                var dynamicPropValue = dynamicsPropertyInfo.GetValue(expectedValues);
                Assert.True(Equals(dynamicPropValue, GetOfferPropertyValue()));

                object GetOfferPropertyValue() => offerPropertyInfo.GetValue(offer);
            }
        }

        [Theory]
        [MemberData(nameof(ShopOffer_Tests_DataGenerator.GetOffersWithExpectedCurrencies), MemberType = typeof(ShopOffer_Tests_DataGenerator))]
        void Currency_Check(HtmlNode offerNode, Currency expectedCurrency)
        {
            var offer = ShopOffer.ParseNode(offerNode);
            Assert.True(expectedCurrency == offer.Currency);
        }

        [Theory]
        [MemberData(nameof(ShopOffer_Tests_DataGenerator.GetItemsWithDifferentRawRepairCost), MemberType = typeof(ShopOffer_Tests_DataGenerator))]
        void ShopOffer_RepairCostAndWinItemsCheck(HtmlNode offerNode, int? expectedRepairCost, WinItem[] expectedWinItems)
        {
            var offer = ShopOffer.ParseNode(offerNode);
            if (expectedRepairCost.HasValue)
            {
                Assert.False(offer.HasWinItems);
                Assert.Equal(expectedRepairCost, offer.RepairCost);
                Assert.Throws<InvalidOperationException>(() => offer.WinItems);
            }
            else
            {
                Assert.True(offer.HasWinItems);
                Assert.Equal(expectedWinItems, offer.WinItems);
                Assert.Throws<InvalidOperationException>(() => offer.RepairCost);
            }
        }
    }

